                              <div style={{ color: '#9CA3AF' }}>  </div>
                            ) : showOfferActions ? (
                              <div style={{ display: 'flex', gap: 8 }}>
                                <Button
                                  type="primary"
                                  style={{ background: '#10B981', borderColor: '#10B981' }}
                                  onClick={() => handleAcceptOffer(msg.id)}
                                  block
                                >
                                  
                                </Button>
                                <Button danger onClick={() => handleRejectOffer(msg.id)} block>
                                  
                                </Button>
                              </div>
                            ) : (
                              <div style={{ color: '#9CA3AF' }}>  </div>
                            )}

                            <div style={{ marginTop: 8 }}>
                              <Text type="secondary" style={{ fontSize: 11 }}>
                                {formatMessageTime(msg.created_at)}
                              </Text>
                            </div>
                          </Card>
                        ) : (
                          <>
                            {msg.text ? (
                              <Text style={{ 
                                fontSize: isMobile ? 15 : 16, 
                                color: msg.is_mine ? '#ffffff' : '#1f2937',
                                display: 'block',
                                marginBottom: 4
                              }}>
                                {msg.text}
                              </Text>
                            ) : null}
                            {msg.file_url && msg.file_name ? (
                              <div style={{ marginTop: msg.text ? 8 : 0, marginBottom: 4 }}>
                                <a
                                  href={msg.file_url}
                                  target="_blank"
                                  rel="noopener noreferrer"
                                  style={{ 
                                    color: msg.is_mine ? '#fff' : '#1890ff',
                                    fontSize: isMobile ? 13 : 14
                                  }}
                                >
                                   {msg.file_name}
                                </a>
                              </div>
                            ) : null}
                            {showWorkActions ? (
                              <div style={{ display: 'flex', gap: 8, marginTop: 10 }}>
                                <Button
                                  type="primary"
                                  style={{ background: '#10B981', borderColor: '#10B981' }}
                                  onClick={handleApproveOrder}
                                  block
                                >
                                   
                                </Button>
                                <Button danger onClick={handleRequestRevision} block>
                                   
                                </Button>
                              </div>
                            ) : null}
                            <Text style={{ 
                              fontSize: isMobile ? 11 : 12, 
                              color: msg.is_mine ? 'rgba(255, 255, 255, 0.7)' : '#9ca3af'
                            }}>
                              {formatMessageTime(msg.created_at)}
                              {msg.is_mine && (
                                <span
                                  style={{
                                    marginLeft: 6,
                                    display: 'inline-flex',
                                    alignItems: 'center',
                                    transform: 'translateY(1px)',
                                    color: msg.is_read ? 'rgba(255, 255, 255, 0.95)' : 'rgba(255, 255, 255, 0.75)'
                                  }}
                                >
                                  {msg.is_read ? (
                                    <span
                                      style={{
                                        position: 'relative',
                                        width: isMobile ? 16 : 18,
                                        height: isMobile ? 12 : 14,
                                        display: 'inline-block'
                                      }}
                                    >
                                      <CheckOutlined
                                        style={{
                                          position: 'absolute',
                                          left: 0,
                                          top: 0,
                                          fontSize: isMobile ? 11 : 12
                                        }}
                                      />
                                      <CheckOutlined
                                        style={{
                                          position: 'absolute',
                                          left: isMobile ? 6 : 7,
                                          top: 0,
                                          fontSize: isMobile ? 11 : 12
                                        }}
                                      />
                                    </span>
                                  ) : (
                                    <CheckOutlined style={{ fontSize: isMobile ? 11 : 12 }} />
                                  )}
                                </span>
                              )}
                            </Text>
                          </>
                        )}
                      </div>
                    </div>
                  );
                })}
                <div ref={messagesEndRef} />
              </div>
            ) : (
              <div style={{ 
                textAlign: 'center', 
                color: '#9ca3af', 
                paddingTop: isMobile ? '50px' : '100px',
                fontSize: isMobile ? 12 : 14
              }}>
                <MessageOutlined style={{ fontSize: isMobile ? 36 : 48, color: '#d1d5db', marginBottom: isMobile ? 12 : 16, display: 'block' }} />
                    
              </div>
            )}
          </div>

          {/* Input Area */}
          {selectedChat && (
            <div style={{ 
              padding: isMobile ? '8px 12px 12px 12px' : '16px',
              borderTop: '1px solid #e5e7eb',
              background: '#ffffff',
              flexShrink: 0
            }}>
              <div style={{ 
                display: 'flex',
                gap: isMobile ? 6 : 8,
                alignItems: 'flex-end'
              }}>
                <Input.TextArea
                  value={messageText}
                  onChange={(e) => setMessageText(e.target.value)}
                  placeholder=" ..."
                  autoSize={{ minRows: isMobile ? 2 : 3, maxRows: isMobile ? 6 : 8 }}
                  style={{ 
                    flex: 1,
                    borderRadius: 8,
                    border: '1px solid #d1d5db',
                    fontSize: isMobile ? 13 : 14
                  }}
                  onKeyPress={(e) => {
                    if (e.key === 'Enter' && !e.shiftKey) {
                      e.preventDefault();
                      sendMessage();
                    }
                  }}
                  disabled={sending}
                />
                
                {/* File Attachment Button */}
                <Upload
                  beforeUpload={handleFileSelect}
                  showUploadList={false}
                  multiple
                  accept=".doc,.docx,.pdf,.rtf,.txt,.odt,.ppt,.pptx,.xls,.xlsx,.csv,.dwg,.dxf,.cdr,.cdw,.bak,.jpg,.jpeg,.png,.gif,.bmp,.svg,.zip,.rar,.7z"
                >
                  <Button
                    type="text"
                    icon={<PaperClipOutlined />}
                    style={{ 
                      width: isMobile ? 36 : 40, 
                      height: isMobile ? 36 : 40,
                      border: '1px solid #d1d5db',
                      borderRadius: 8,
                      color: '#6b7280',
                      fontSize: isMobile ? 14 : 16
                    }}
                    disabled={sending}
                    title=" "
                  />
                </Upload>

                <Button
                  type="primary"
                  shape="circle"
                  icon={<SendOutlined />}
                  style={{ 
                    width: isMobile ? 36 : 40, 
                    height: isMobile ? 36 : 40,
                    background: '#3b82f6',
                    border: 'none',
                    fontSize: isMobile ? 14 : 16
                  }}
                  onClick={sendMessage}
                  loading={sending}
                  disabled={!messageText.trim() && attachedFiles.length === 0}
                />
              </div>

              {attachedFiles.length > 0 && (
                <div style={{
                  marginTop: 10,
                  paddingTop: 10,
                  borderTop: '1px solid #f3f4f6',
                  display: 'flex',
                  flexWrap: 'wrap',
                  gap: 8
                }}>
                  {attachedFiles.map((file, index) => (
                    <div
                      key={`${file.name}-${file.size}-${index}`}
                      style={{
                        display: 'flex',
                        alignItems: 'center',
                        gap: 8,
                        padding: '6px 10px',
                        background: '#f9fafb',
                        border: '1px solid #e5e7eb',
                        borderRadius: 999,
                        maxWidth: '100%'
                      }}
                    >
                      <FileOutlined style={{ color: '#6b7280' }} />
                      <Text
                        style={{
                          fontSize: 12,
                          color: '#374151',
                          maxWidth: isMobile ? 160 : 240
                        }}
                        ellipsis
                      >
                        {file.name}
                      </Text>
                      <Text style={{ fontSize: 11, color: '#9ca3af', whiteSpace: 'nowrap' }}>
                        {(file.size / 1024 / 1024).toFixed(2)} 
                      </Text>
                      <Button
                        type="text"
                        size="small"
                        onClick={() => removeAttachedFile(file)}
                        style={{
                          color: '#ef4444',
                          padding: 0,
                          height: 20,
                          minWidth: 20,
                          lineHeight: '20px'
                        }}
                      >
                        
                      </Button>
                    </div>
                  ))}
                </div>
              )}
            </div>
          )}
        </div>
      </div>
      </ErrorBoundary>
      <Modal
        open={reviewModalOpen}
        centered
        onCancel={() => setReviewModalOpen(false)}
        onOk={handleSubmitReview}
        okButtonProps={{ loading: reviewSubmitting }}
        okText=""
        cancelText=""
        title="   "
        destroyOnClose
      >
        <div style={{ display: 'flex', flexDirection: 'column', gap: 12 }}>
          <div>
            <Text style={{ display: 'block', marginBottom: 6 }}></Text>
            <Rate value={reviewRating} onChange={(v) => setReviewRating(v)} />
          </div>
          <div>
            <Text style={{ display: 'block', marginBottom: 6 }}></Text>
            <Input.TextArea
              value={reviewComment}
              onChange={(e) => setReviewComment(e.target.value)}
              placeholder="   ()"
              autoSize={{ minRows: 3, maxRows: 6 }}
            />
          </div>
        </div>
      </Modal>
      <IndividualOfferModal
        open={offerModalOpen}
        onClose={() => setOfferModalOpen(false)}
        onSubmit={handleOfferSubmit}
      />
    </Modal>
  );
};

export default MessageModalNew;

import React, { useState } from 'react';
import { Card, Typography, Tag, Button, Space, Empty, Spin, Input, Select, Row, Col, InputNumber, message, Avatar, Divider, Tooltip } from 'antd';
import { ClockCircleOutlined, SearchOutlined, FilterOutlined, UserOutlined, DeleteOutlined, FileOutlined, FilePdfOutlined, FileWordOutlined, FileImageOutlined, FileZipOutlined, DownloadOutlined, ShareAltOutlined } from '@ant-design/icons';
import { useQuery } from '@tanstack/react-query';
import { useNavigate } from 'react-router-dom';
import { ordersApi, type Order, type OrderFile } from '../../api/orders';
import { catalogApi, type Subject, type WorkType } from '../../api/catalog';
import { authApi } from '../../api/auth';
import { ORDER_STATUS_COLORS, ORDER_STATUS_TEXTS } from '../../config/orderStatuses';
import dayjs from 'dayjs';
import relativeTime from 'dayjs/plugin/relativeTime';
import 'dayjs/locale/ru';
import styles from './OrdersFeed.module.css';
import { formatCurrency } from '../../utils/formatters';
import BidModal from './BidModal';

dayjs.extend(relativeTime);
dayjs.locale('ru');

const { Title, Text, Paragraph } = Typography;

type OrdersFeedOrder = Order & {
  subject_id?: number;
  work_type_id?: number;
  responses_count?: number;
  user_has_bid?: boolean;
  is_active?: boolean;
  deleted?: boolean;
  subject_name?: string;
  work_type_name?: string;
  client_avatar?: string;
  client_orders_count?: number;
  custom_subject?: string;
  custom_work_type?: string;
  files?: Array<
    Partial<OrderFile> & {
      id?: number | string;
      filename?: string;
      file_name?: string;
      file_size?: string;
      file_url?: string | null;
      view_url?: string | null;
      download_url?: string | null;
      file?: string | null;
      file_type?: string;
    }
  >;
};

const OrdersFeed: React.FC = () => {
  const navigate = useNavigate();
  const [searchText, setSearchText] = useState('');
  const [selectedSubject, setSelectedSubject] = useState<number | undefined>();
  const [selectedWorkType, setSelectedWorkType] = useState<number | undefined>();
  const [budgetRange, setBudgetRange] = useState<[number, number]>([0, 30000]);
  const [responsesFilter, setResponsesFilter] = useState<string>('all');
  const [showFilters, setShowFilters] = useState(false);
  const [isMobile, setIsMobile] = useState(window.innerWidth <= 840);
  const [bidModalVisible, setBidModalVisible] = useState(false);
  const [selectedOrderForBid, setSelectedOrderForBid] = useState<OrdersFeedOrder | null>(null);
  const [myBidsByOrderId, setMyBidsByOrderId] = useState<Record<number, boolean | 'loading'>>({});

  //   
  const { data: userProfile } = useQuery({
    queryKey: ['user-profile'],
    queryFn: () => authApi.getCurrentUser(),
  });

  const { data: fetchedSubjects = [] } = useQuery<Subject[]>({
    queryKey: ['subjects'],
    queryFn: () => catalogApi.getSubjects(),
  });

  React.useEffect(() => {
    const handleResize = () => {
      setIsMobile(window.innerWidth <= 840);
    };
    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  //   (     )
  const { data: ordersData, isLoading: ordersLoading } = useQuery<OrdersFeedOrder[]>({
    queryKey: ['orders-feed'],
    queryFn: async () => {
      console.log('    API...');
      console.log('  :', userProfile);
      console.log('  :', userProfile?.role);
      const data = await ordersApi.getAvailableOrders();
      console.log('  :', data);
      console.log('  :', data?.results?.length || data?.length || 0);
      if ((data?.results?.length || data?.length || 0) === 0) {
        console.warn('  !  :');
        if (userProfile?.role === 'client') {
          console.warn('        -       ');
          console.warn('    :      https://okoznaniy.ru/expert');
          console.warn('             ""');
        } else {
          console.warn('   1.      ');
          console.warn('   2.     "new"');
          console.warn('   3.     ');
        }
      }
      return data;
    },
  });

  //  
  const { data: workTypes = [] } = useQuery<WorkType[]>({
    queryKey: ['workTypes'],
    queryFn: () => catalogApi.getWorkTypes(),
  });

  //     API
  const orders = Array.isArray(ordersData)
    ? ordersData.filter((order) => {
        if (!order) return false;
        if (order.is_active === false) return false;
        if (order.deleted === true) return false;
        return !!order.id && !!order.title;
      })
    : [];

  React.useEffect(() => {
    if (userProfile?.role !== 'expert') return;
    if (typeof userProfile?.id !== 'number') return;
    if (!Array.isArray(orders) || orders.length === 0) return;

    let cancelled = false;
    const toFetch = orders.filter((o) => {
      if (typeof o?.id !== 'number') return false;
      if (typeof o.user_has_bid === 'boolean') return false;
      if (Array.isArray(o.bids)) return false;
      return myBidsByOrderId[o.id] === undefined;
    });

    if (toFetch.length === 0) return;

    setMyBidsByOrderId((prev) => {
      const next = { ...prev };
      for (const o of toFetch) next[o.id] = 'loading';
      return next;
    });

    (async () => {
      for (const o of toFetch) {
        try {
          const bids = await ordersApi.getBids(o.id);
          const hasBid = Array.isArray(bids)
            ? bids.some((bid) => bid.expert?.id === userProfile.id && (bid.status || 'active') === 'active')
            : false;
          if (!cancelled) {
            setMyBidsByOrderId((prev) => ({ ...prev, [o.id]: hasBid }));
          }
        } catch {
          if (!cancelled) {
            setMyBidsByOrderId((prev) => ({ ...prev, [o.id]: false }));
          }
        }
      }
    })();

    return () => {
      cancelled = true;
    };
  }, [orders, userProfile?.role, userProfile?.id, myBidsByOrderId]);

  //  
  const filteredOrders = orders.filter((order) => {
    const matchesSearch = !searchText || 
      order.title?.toLowerCase().includes(searchText.toLowerCase()) ||
      order.description?.toLowerCase().includes(searchText.toLowerCase());
    
    const matchesSubject = !selectedSubject || order.subject_id === selectedSubject;
    const matchesWorkType = !selectedWorkType || order.work_type_id === selectedWorkType;
    
    const orderBudget = Number(order.budget);
    const matchesBudget =
      Number.isFinite(orderBudget) && orderBudget >= budgetRange[0] && orderBudget <= budgetRange[1];
    
    const matchesResponses = 
      responsesFilter === 'all' ||
      (responsesFilter === 'none' && order.responses_count === 0) ||
      (responsesFilter === 'few' && order.responses_count > 0 && order.responses_count <= 5) ||
      (responsesFilter === 'many' && order.responses_count > 5);

    return matchesSearch && matchesSubject && matchesWorkType && matchesBudget && matchesResponses;
  });

  const getStatusColor = (status: string) => ORDER_STATUS_COLORS[status] || 'default';
  const getStatusText = (status: string) => ORDER_STATUS_TEXTS[status] || status;

  // ,     
  const isOrderOwner = (order: OrdersFeedOrder) => {
    return order.client?.id === userProfile?.id || 
           order.client_id === userProfile?.id;
  };

  //  
  const handleDeleteOrder = async (orderId: number) => {
    try {
      await ordersApi.deleteOrder(orderId);
      message.success('  ');
      //   
      window.location.reload();
    } catch (error: unknown) {
      const errorMessage =
        (error as { response?: { data?: { detail?: string } } })?.response?.data?.detail ||
        '   ';
      message.error(errorMessage);
    }
  };

  const handleDownloadOrderFile = React.useCallback(
    async (orderId: number, file: OrdersFeedOrder['files'][number]) => {
    try {
      const fileIdNum = Number(file?.id);
      const filename = file?.filename || file?.file_name || 'file';

      if (!fileIdNum || Number.isNaN(fileIdNum)) {
        const url = file?.view_url || file?.file_url || file?.file;
        if (url) {
          window.open(url, '_blank', 'noopener,noreferrer');
          return;
        }
        message.error('   ');
        return;
      }

      const blob = await ordersApi.downloadOrderFile(orderId, fileIdNum);
      const blobUrl = window.URL.createObjectURL(blob);
      const a = document.createElement('a');
      a.href = blobUrl;
      a.download = filename;
      document.body.appendChild(a);
      a.click();
      a.remove();
      window.URL.revokeObjectURL(blobUrl);
    } catch (e: unknown) {
      const status = (e as { response?: { status?: number } })?.response?.status;
      if (status === 401) {
        message.error('    ');
      } else {
        message.error('   ');
      }
    }
    },
    []
  );

  return (
    <div className={styles.contentContainer}>
      {/*     */}
      {!isMobile && (
        <div className={styles.pageHeader}>
          <div className={styles.headerContent}>
            <Title level={2} style={{ margin: 0, marginBottom: 8 }}>
               
            </Title>
            <Text type="secondary">
                   
            </Text>
          </div>
          <Button 
            type="primary" 
            size="large"
            onClick={() => navigate('/create-order')}
            className={styles.primaryButton}
          >
             
          </Button>
        </div>
      )}

      {/*  */}
      <Card 
        className={styles.filterCard}
      >
        <Row gutter={[16, 16]}>
          <Col xs={24} sm={24} md={12} lg={8}>
            <Input
              size="large"
              placeholder="    ..."
              prefix={<SearchOutlined />}
              value={searchText}
              onChange={(e) => setSearchText(e.target.value)}
              allowClear
            />
          </Col>
          <Col xs={24} sm={12} md={6} lg={8}>
            <Select
              size="large"
              placeholder=""
              style={{ width: '100%' }}
              value={selectedSubject}
              onChange={setSelectedSubject}
              allowClear
              suffixIcon={<FilterOutlined />}
            >
              {(fetchedSubjects || []).map((subject: Subject) => (
                <Select.Option key={subject.id} value={subject.id}>
                  {subject.name}
                </Select.Option>
              ))}
            </Select>
          </Col>
          <Col xs={24} sm={12} md={6} lg={8}>
            <Select
              size="large"
              placeholder=" "
              style={{ width: '100%' }}
              value={selectedWorkType}
              onChange={setSelectedWorkType}
              allowClear
              suffixIcon={<FilterOutlined />}
            >
              {(workTypes || []).map((workType: WorkType) => (
                <Select.Option key={workType.id} value={workType.id}>
                  {workType.name}
                </Select.Option>
              ))}
            </Select>
          </Col>
        </Row>

        {/*   */}
        <div style={{ marginTop: 16 }}>
          <Button 
            type="link" 
            onClick={() => setShowFilters(!showFilters)}
            style={{ padding: 0, marginBottom: showFilters ? 16 : 0 }}
          >
            {showFilters ? ' ' : '  '}
          </Button>
        </div>

        {showFilters && (
          <Row gutter={[16, 16]} style={{ marginTop: 8 }}>
            <Col xs={24} sm={12} md={8}>
              <div style={{ marginBottom: 8 }}>
                <Text strong></Text>
              </div>
              <div style={{ display: 'flex', gap: 12, alignItems: 'center' }}>
                <div style={{ display: 'flex', alignItems: 'center', gap: 8 }}>
                  <Text style={{ whiteSpace: 'nowrap' }}></Text>
                  <InputNumber
                    size="large"
                    min={0}
                    max={budgetRange[1]}
                    value={budgetRange[0]}
                    onChange={(value) => setBudgetRange([value || 0, budgetRange[1]])}
                    placeholder="0"
                    controls={false}
                    style={{ width: 120 }}
                    formatter={(value) => `${value} `}
                    parser={(value) => {
                      const num = Number(String(value ?? '').replace(/[^\d.-]/g, ''));
                      return Number.isFinite(num) ? num : 0;
                    }}
                  />
                </div>
                <div style={{ display: 'flex', alignItems: 'center', gap: 8 }}>
                  <Text style={{ whiteSpace: 'nowrap' }}></Text>
                  <InputNumber
                    size="large"
                    min={budgetRange[0]}
                    max={100000}
                    value={budgetRange[1]}
                    onChange={(value) => setBudgetRange([budgetRange[0], value || 30000])}
                    placeholder="30000"
                    controls={false}
                    style={{ width: 120 }}
                    formatter={(value) => `${value} `}
                    parser={(value) => {
                      const num = Number(String(value ?? '').replace(/[^\d.-]/g, ''));
                      return Number.isFinite(num) ? num : 0;
                    }}
                  />
                </div>
              </div>
            </Col>
            <Col xs={24} sm={12} md={8}>
              <div style={{ marginBottom: 8 }}>
                <Text strong> </Text>
              </div>
              <Select
                size="large"
                placeholder=" "
                style={{ width: '100%' }}
                value={responsesFilter}
                onChange={setResponsesFilter}
              >
                <Select.Option value="all"> </Select.Option>
                <Select.Option value="none"> </Select.Option>
                <Select.Option value="few">1-5 </Select.Option>
                <Select.Option value="many"> 5 </Select.Option>
              </Select>
            </Col>
            <Col xs={24} sm={12} md={8}>
              <div style={{ marginBottom: 8 }}>
                <Text strong> </Text>
              </div>
              <div style={{ 
                fontSize: 24, 
                fontWeight: 600, 
                color: '#667eea',
                lineHeight: '40px'
              }}>
                {filteredOrders.length}
              </div>
            </Col>
          </Row>
        )}
      </Card>

      {/*     */}
      {userProfile?.role === 'client' && filteredOrders.length === 0 && !ordersLoading && (
        <Card 
          className={styles.infoCard}
        >
          <div style={{ textAlign: 'center', padding: '20px' }}>
            <Title level={4} style={{ color: 'white', marginBottom: 16 }}>
               :    ?
            </Title>
            <Paragraph style={{ color: 'white', fontSize: 16, marginBottom: 20 }}>
                  <strong> </strong>  .<br />
                   <strong> </strong>.
            </Paragraph>
            <Space size={12}>
              <Button 
                type="default"
                size="large"
                onClick={() => navigate('/expert')}
                style={{
                  background: 'white',
                  color: '#667eea',
                  border: 'none',
                  borderRadius: 8,
                  fontWeight: 600,
                  height: 48,
                  padding: '0 32px'
                }}
              >
                  
              </Button>
              <Button 
                size="large"
                onClick={() => navigate('/create-order')}
                style={{
                  background: 'rgba(255, 255, 255, 0.2)',
                  color: 'white',
                  border: '2px solid white',
                  borderRadius: 8,
                  fontWeight: 600,
                  height: 48,
                  padding: '0 32px'
                }}
              >
                 
              </Button>
            </Space>
          </div>
        </Card>
      )}

      {/*   */}
      {ordersLoading ? (
        <div style={{ textAlign: 'center', padding: '60px 0' }}>
          <Spin size="large" />
        </div>
      ) : filteredOrders.length === 0 ? (
        <Empty
          description={
            <div>
              <Text style={{ fontSize: 16, color: '#999' }}>
                {searchText || selectedSubject || selectedWorkType 
                  ? '  .   .'
                  : userProfile?.role === 'client' 
                    ? '       '
                    : '   '}
              </Text>
            </div>
          }
          style={{ padding: '60px 0' }}
        >
          {userProfile?.role !== 'client' && (
            <Button 
              type="primary" 
              size="large"
              onClick={() => navigate('/create-order')}
            >
                
            </Button>
          )}
        </Empty>
      ) : (
        <div style={{ display: 'grid', gap: 16 }}>
          {filteredOrders.map((order: OrdersFeedOrder) => {
            //     
            if (order.files) {
              console.log(` #${order.id}  ${order.files.length} :`, order.files);
            }

            const cachedMyBid = typeof order.id === 'number' ? myBidsByOrderId[order.id] : undefined;
            const checkingMyBid = cachedMyBid === 'loading';
            const hasMyBid =
              userProfile?.role === 'expert'
                ? (typeof order.user_has_bid === 'boolean'
                    ? order.user_has_bid
                    : (Array.isArray(order.bids) && order.bids.some((bid) => bid.expert?.id === userProfile?.id))
                      ? true
                      : (typeof cachedMyBid === 'boolean' ? cachedMyBid : false))
                : false;
            
            return (
            <Card
              key={order.id}
              hoverable
              className={styles.orderCard}
              styles={{ body: { padding: 24 } }}
              onClick={() => navigate(`/orders/${order.id}`)}
            >
              <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'flex-start', marginBottom: 16 }}>
                <div style={{ flex: 1 }}>
                  <Title 
                    level={4} 
                    style={{ 
                      margin: 0, 
                      marginBottom: 12, 
                      fontSize: 20, 
                      fontWeight: 700,
                      color: '#1890ff',
                      cursor: 'pointer',
                    }}
                  >
                    {order.title}
                  </Title>
                  <Space size={8} wrap>
                    <Tag className={styles.statusTag} color={getStatusColor(order.status)}>
                      {getStatusText(order.status)}
                    </Tag>
                    {(order.custom_subject || order.subject?.name || order.subject_name) && (
                      <Tag className={styles.subjectTag}>
                        {order.custom_subject || order.subject?.name || order.subject_name}
                      </Tag>
                    )}
                    {(order.custom_work_type || order.work_type?.name || order.work_type_name) && (
                      <Tag className={styles.workTypeTag}>
                        {order.custom_work_type || order.work_type?.name || order.work_type_name}
                      </Tag>
                    )}
                    {order.topic?.name && (
                      <Tag className={styles.topicTag}>
                        : {order.topic.name}
                      </Tag>
                    )}
                  </Space>
                </div>
                <div style={{ textAlign: 'right', marginLeft: 16 }}>
                  <div style={{ display: 'flex', justifyContent: 'flex-end', marginBottom: 8 }}>
                    <Tooltip title="   ">
                      <Button
                        type="text"
                        size="small"
                        icon={<ShareAltOutlined />}
                        onClick={async (e) => {
                          e.stopPropagation();
                          const url = `${window.location.origin}/orders/${order.id}`;
                          try {
                            await navigator.clipboard.writeText(url);
                            message.success(' ');
                          } catch {
                            message.error('   ');
                          }
                        }}
                      />
                    </Tooltip>
                  </div>
                  <div className={styles.budgetText}>
                    {Number.isFinite(Number(order.budget)) ? formatCurrency(Number(order.budget)) : ''}
                  </div>
                </div>
              </div>

              <Paragraph 
                ellipsis={{ rows: 2 }}
                style={{ color: '#666', marginBottom: 16 }}
              >
                {order.description}
              </Paragraph>

              {/*   */}
              {order.files && order.files.length > 0 && (
                <div style={{ marginBottom: 16 }}>
                  <Text type="secondary" style={{ fontSize: 12, marginBottom: 8, display: 'block' }}>
                      ({order.files.length}):
                  </Text>
                  <Space size={8} wrap>
                    {order.files.map((file) => {
                      //     
                      const getFileIcon = (filename: string) => {
                        const ext = filename.split('.').pop()?.toLowerCase();
                        if (ext === 'pdf') return <FilePdfOutlined style={{ color: '#ff4d4f' }} />;
                        if (['doc', 'docx'].includes(ext || '')) return <FileWordOutlined style={{ color: '#1890ff' }} />;
                        if (['jpg', 'jpeg', 'png', 'gif', 'svg'].includes(ext || '')) return <FileImageOutlined style={{ color: '#52c41a' }} />;
                        if (['zip', 'rar', '7z'].includes(ext || '')) return <FileZipOutlined style={{ color: '#fa8c16' }} />;
                        return <FileOutlined style={{ color: '#666' }} />;
                      };
                      const fileName = file.filename || file.file_name || 'file';

                      return (
                        <Tooltip
                          key={String(file.id ?? fileName)}
                          title={` ${fileName} (${file.file_size || ' '})`}
                        >
                          <Tag 
                            icon={getFileIcon(fileName)}
                            className={styles.fileTag}
                            onClick={() => {
                              handleDownloadOrderFile(order.id, file);
                            }}
                          >
                            {fileName} <DownloadOutlined style={{ marginLeft: 4 }} />
                          </Tag>
                        </Tooltip>
                      );
                    })}
                  </Space>
                </div>
              )}

              <Space size={16} wrap style={{ marginBottom: 16 }}>
                <Space size={4}>
                  <ClockCircleOutlined style={{ color: '#999' }} />
                  <Text type="secondary" style={{ fontSize: 14 }}>
                    {order.deadline ? dayjs(order.deadline).fromNow() : ' '}
                  </Text>
                </Space>
                {order.created_at && (
                  <Text type="secondary" style={{ fontSize: 14 }}>
                     {dayjs(order.created_at).fromNow()}
                  </Text>
                )}
                <Space size={4}>
                  <UserOutlined style={{ color: '#999' }} />
                  <Text 
                    style={{ 
                      fontSize: 14, 
                      fontWeight: 600,
                      color: (order.bids?.length || order.responses_count || 0) === 0 ? '#999' : 
                             (order.bids?.length || order.responses_count || 0) > 5 ? '#ff4d4f' : '#52c41a'
                    }}
                  >
                    {order.bids?.length || order.responses_count || 0}
                  </Text>
                </Space>
              </Space>

              <Divider style={{ margin: '16px 0' }} />

              <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', flexWrap: 'wrap', gap: 12 }}>
                <Space size={10}>
                  <Avatar 
                    size={24}
                    src={order.client?.avatar || order.client_avatar || userProfile?.avatar}
                    icon={<UserOutlined />}
                    style={{ backgroundColor: '#667eea' }}
                  />
                  <div>
                    <Text strong style={{ display: 'block', fontSize: 14 }}>
                      {order.client?.username || order.client_name || 
                       (order.client?.first_name && order.client?.last_name 
                         ? `${order.client.first_name} ${order.client.last_name}` 
                         : userProfile?.username || '')}
                    </Text>
                    <Text type="secondary" style={{ fontSize: 12 }}>
                      : {order.client_orders_count || 1}
                    </Text>
                  </div>
                </Space>
                <Space size={8}>
                  {isOrderOwner(order) ? (
                    <Button 
                      danger
                      icon={<DeleteOutlined />}
                      className={`${styles.actionButton} ${styles.deleteButton}`}
                      onClick={(e) => {
                        e.stopPropagation(); //    
                        if (window.confirm(' ,     ?')) {
                          handleDeleteOrder(order.id);
                        }
                      }}
                    >
                      
                    </Button>
                  ) : userProfile?.role === 'expert' ? (
                    <Button 
                      type={hasMyBid ? 'default' : 'primary'}
                      disabled={hasMyBid || checkingMyBid}
                      className={`${styles.actionButton} ${styles.bidButton}`}
                      onClick={(e) => {
                        e.stopPropagation(); //    
                        if (hasMyBid || checkingMyBid) return;
                        setSelectedOrderForBid(order);
                        setBidModalVisible(true);
                      }}
                    >
                      {hasMyBid ? '     ' : checkingMyBid ? '...' : ''}
                    </Button>
                  ) : null}
                </Space>
              </div>
            </Card>
          );
          })}
        </div>
      )}

            {/*     */}
            {isMobile && (
              <Button 
                type="primary" 
                size="large"
                block
                onClick={() => navigate('/create-order')}
                style={{
                  background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
                  border: 'none',
                  borderRadius: 12,
                  height: 48,
                  fontSize: 16,
                  fontWeight: 500,
                  boxShadow: '0 4px 12px rgba(102, 126, 234, 0.3)',
                  marginBottom: 16
                }}
              >
                 
              </Button>
            )}

      {selectedOrderForBid && (
        <BidModal
          visible={bidModalVisible}
          onClose={() => {
            setBidModalVisible(false);
            setSelectedOrderForBid(null);
          }}
          orderId={selectedOrderForBid.id}
          orderTitle={selectedOrderForBid.title}
          orderBudget={Number.isFinite(Number(selectedOrderForBid.budget)) ? Number(selectedOrderForBid.budget) : undefined}
        />
      )}
    </div>
  );
};

export default OrdersFeed;
